import React, { useEffect, useRef } from 'react';

interface VimeoPlayerProps {
  onReachThreshold: () => void;
}

declare const Vimeo: any;

const VimeoPlayer: React.FC<VimeoPlayerProps> = ({ onReachThreshold }) => {
  const containerRef = useRef<HTMLDivElement>(null);
  const playerRef = useRef<any>(null);
  const hasTriggered = useRef(false);

  useEffect(() => {
    // Vimeo Video ID from request: 1165329797
    const videoId = "1165329797";

    const options = {
      id: videoId,
      autoplay: true,
      muted: true,
      loop: false,
      responsive: true,
      title: false,
      byline: false,
      portrait: false,
      controls: true, // Minimal controls but needed for basic interaction
    };

    if (containerRef.current && typeof Vimeo !== 'undefined') {
      playerRef.current = new Vimeo.Player(containerRef.current, options);

      playerRef.current.on('timeupdate', (data: { percent: number }) => {
        // Trigger modal at 90% completion
        if (data.percent >= 0.9 && !hasTriggered.current) {
          hasTriggered.current = true;
          onReachThreshold();
        }
      });
    }

    return () => {
      if (playerRef.current) {
        playerRef.current.unload();
      }
    };
  }, [onReachThreshold]);

  return (
    <div className="vimeo-wrapper bg-black">
      <div ref={containerRef} className="w-full h-full"></div>
    </div>
  );
};

